mapper.insert:
INSERT 
  INTO	
  		CP_NOTICE
  	 (
	 	USER_NO
	 ,	TITLE
	 , 	CONTENT
	 , 	CREATE_DATE
	 )
VALUES 
	 (
	   	#{userNo}
	 , 	#{title} 	
	 , 	#{content} 	
	 , 	SYSDATE	
	 )

INSERT
  INTO
  		CP_NT_FILE
  	 (
		NOTICE_NO
	 ,	FILE_URL
	 )
VALUES
	 (
		#{noticeNo}
	 ,	#{fileUrl}
	 )

mapper.update:
UPDATE
		CP_NOTICE
   SET
   		TITLE = #{title}
   	 ,	CONTENT = #{content}
 WHERE	
 		NOTICE_NO = #{noticeNo}
