mapper.insert:
INSERT 
  INTO	
  		CP_REPORT
  	 (
	 	USER_NO
	 ,	TITLE
	 , 	CONTENT
	 , 	CREATE_DATE
	 )
VALUES 
	 (
	   	#{userNo}
	 , 	#{title} 	
	 , 	#{content} 	
	 , 	SYSDATE 	
	 )

INSERT
  INTO	
  		CP_RP_FILE
  	 (
		REPORT_NO
	 ,	FILE_URL
	 )
VALUES
	 (
		#{reportNo}
	 ,	#{fileUrl}
	 )

mapper.update:
UPDATE
		CP_REPORT
   SET
   		TITLE = #{title}
   	 ,	CONTENT = #{content}
 WHERE	
 		REPORT_NO = #{reportNo}
