mapper.insert:
INSERT 
  INTO	
  		CP_REVIEW
  	 (
	 	USER_NO
	 ,	TITLE
	 , 	CONTENT
	 , 	CREATE_DATE
	 )
VALUES 
	 (
	   	#{userNo}
	 , 	#{title} 	
	 , 	#{content} 	
	 , 	SYSDATE 	
	 )

INSERT
  INTO
  		CP_RV_FILE
  	 (
		REVIEW_NO
	 ,	FILE_URL
	 )
VALUES
	 (
		#{reviewNo}
	 ,	#{fileUrl}
	 )

mapper.update:
UPDATE
		CP_REVIEW
   SET
   		TITLE = #{title}
   	 ,	CONTENT = #{content}
 WHERE	
 		REVIEW_NO = #{reviewNo}
