mapper.insert:
INSERT 
  INTO	

  			CP_NT_COMMENT
  			CP_RP_COMMENT
  			CP_RV_COMMENT
			TYPE_ERROR_TABLE

  	 (	

  			NOTICE_NO
  			REPORT_NO
  			REVIEW_NO
			TYPE_ERROR_COLUMN

	 ,	USER_NO
	 , 	CONTENT
	 )
VALUES 
	 (

  			#{noticeNo}
  			#{reportNo}
  			#{reviewNo}
			TYPE_ERROR_VALUES

	 , 	#{userNo} 	
	 , 	#{content} 	
	 )

mapper.select:
SELECT USER_NO AS userNo FROM
CP_NT_COMMENT CP_RP_COMMENT CP_RV_COMMENT TYPE_ERROR_TABLE
WHERE COMMENT_NO = #{commentNo}

mapper.update:
UPDATE
CP_NT_COMMENT CP_RP_COMMENT CP_RV_COMMENT TYPE_ERROR_TABLE
SET IS_ACTIVE = 'N' WHERE COMMENT_NO = #{commentNo}
