mapper.insert:
INSERT INTO
	CP_RENTAL_CAR_MODEL(
		CAR_MODEL
		, RENT_PRICE
		, HOUR_PRICE
		, CHARGE_TYPE
		, SEAT_COUNT
		, CAR_IMG_URL
	)
VALUES(
	#{carModel}
	, #{rentPrice}
	, #{hourPrice}
	, #{chargeType}
	, #{seatCount}
	, #{carImgURL}
)

mapper.update:
UPDATE
	CP_RENTAL_CAR_MODEL
SET
	CAR_MODEL = #{carModel}
	, RENT_PRICE = #{rentPrice}
	, HOUR_PRICE = #{hourPrice}
	, CHARGE_TYPE = #{chargeType}
	, SEAT_COUNT = #{seatCount}
	, CAR_IMG_URL = #{carImgURL}
WHERE
	MODEL_NO = #{modelNo}
