mapper.insert:
INSERT 
  INTO	
  			CP_NOTICE
  	 	(
				USER_NO
			,	MODIFIER_NO
			,	TITLE
			, CONTENT
			)
VALUES 
			(
				#{userNo}
			, #{modifierNo}
			, #{title} 	
			, #{content} 	
			)

INSERT
  INTO
  			CP_NT_FILE
			(
				NOTICE_NO
			,	FILE_URL
			)
VALUES
			(
				#{noticeNo}
			,	#{fileUrl}
			)

mapper.update:
UPDATE
				CP_NOTICE
   SET
				TITLE = #{title}
			,	CONTENT = #{content}
			,	MODIFIER_NO = #{modifierNo}
			,	MODIFY_DATE = SYSDATE
 WHERE	
 				NOTICE_NO = #{noticeNo}
