mapper.insert:
INSERT INTO
	CP_RENTAL_CAR(
		CAR_ID,
		MODEL_NO,
		PARKING_ID,
		ENROLL_DATE
	)
VALUES(
	#{carId}
	, #{modelNo}
	, #{parkingId}
	, SYSDATE
)

mapper.update:
UPDATE
	CP_RENTAL_CAR
SET
	CAR_ID = #{carId}
	, MODEL_NO = #{modelNo}
	, PARKING_ID = #{parkingId}
	, ENROLL_DATE = SYSDATE
WHERE
	CAR_NO = #{carNo}
